const correctHash="6ca13d52ca70c883e0f0bb101e425a89e8624de51db2d2392593af6a84118090";async function calculateHash(t){const e=new TextEncoder;const a=e.encode(t);const c=await crypto.subtle.digest("SHA-256",a);return Array.from(new Uint8Array(c)).map(t=>t.toString(16).padStart(2,"0")).join("")}async function verifyCode(){const t=document.getElementById("code").value;const e=await calculateHash(t);if(e===correctHash){window.location.href="main.html"}else{alert("验证码错误，请重新输入！")}}