const correctHash="6ca13d52ca70c883e0f0bb101e425a89e8624de51db2d2392593af6a84118090";async function calculateHash(e){const t=new TextEncoder;const a=t.encode(e);const c=await crypto.subtle.digest("SHA-256",a);return Array.from(new Uint8Array(c)).map(e=>e.toString(16).padStart(2,"0")).join("")}async function verifyCode(){const e=document.getElementById("code").value;const t=await calculateHash(e);if(t===correctHash){window.location.href="index.html"}else{alert("验证码错误，请重新输入！")}}